import React, {useEffect, useState } from 'react';

function Posts() {

    const [posts, setPosts] = useState([]);//posts is initialized as an empty array and setPosts is a function that will be used to update the state of posts.
    useEffect(() => {
        fetch('http://localhost:5000/api/posts')
        .then(response => response.json())//convert response to json
        .then(data => setPosts(data))//set the state of posts to the data that was fetched
        .catch((err) => console.error('Error fetching posts:', err));//catch any errors that occur during the fetch
    }, []);//empty array means that this effect will only run once after the component is mounted

    return (
        <div className='container mt-5 ms-5 me-5'>

            {posts.length === 0 }? 
            (<p>No posts available.</p>)
            : 
            (
                posts.map((post, index) => (
                    <div key={index} className='card ms-5 me-5 p-2'>
                        <div className='card-header'>
                            <h5>{post.title}</h5>
                        </div>
                        <div className='card-body'>
                            <p>{post.body}</p>
                        </div>
                    </div>
                ))
            )

        </div>
    );
};

export default Posts;
